import React, { useState } from 'react';
import './App.css';
import Ciudad from "../components/Ciudad.jsx";
import About from "../components/About.jsx";
import { Route } from "react-router-dom";
import Nav from '../components/Nav.jsx';
import Cards from '../components/Cards.jsx';
import { toast } from "react-toastify"
const apiKey = '4ae2636d8dfbdc3044bede63951a019b';


function App() {
  const [cities, setCities] = useState([]);
  function onClose(id) {
    setCities(oldCities => oldCities.filter(c => c.id != id));
  }
  function onSearch(ciudad) {
    //Llamado a la API del clima


    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}`)
      .then(r => { r.json() ; toast.error("Lugar no encontrado")})

      .then((recurso) => {
        if (recurso.main !== undefined) {
          const ciudad = {
            min: Math.round(recurso.main.temp_min),
            max: Math.round(recurso.main.temp_max),
            img: recurso.weather[0].icon,
            id: recurso.id,
            wind: recurso.wind.speed,
            temp: recurso.main.temp,
            name: recurso.name,
            weather: recurso.weather[0].main,
            clouds: recurso.clouds.all,
            latitud: recurso.coord.lat,
            longitud: recurso.coord.lon
          };
          if (!cities.find(el => el.name === ciudad.name)) {
            setCities(oldCities => [...oldCities, ciudad]);
          } else {
            toast.error("Ya agregaste este lugar");
          }
        }
      })

  }
  function onFilter(ciudadId) {
    let ciudad = cities.filter((c) => c.id == parseInt(ciudadId));
    if (ciudad.length > 0) {
      toast.success('Ciudad eliminada con exito');
      return ciudad[0];


    } else {

      return (


        <p>No hay ciudades para mostrar</p>
      );
    }
  }

  const Rutas = (
    <>
      <Route exact path="/about" component={About}></Route>
      <Route exact path='/ciudad/:ciudadId' render={({ match }) => <Ciudad city={onFilter(match.params.ciudadId)} />}
      />
    </>
  )


  return (
    <div className="App">

      <Nav onSearch={onSearch} />
      {Rutas}
      <div>
        <Route exact path="/"><Cards
          cities={cities}
          onClose={onClose}
        /></Route>

      </div>
      <hr />
      <footer className="footer">
        <p>Santiago Rosales 2020</p>
      </footer>
    </div>
  );
}

export default App;
